SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select b.chinese_name, b.card_id, p.product_name, --m.maintenance_endtime-m.maintenance_day as startDay, --m.maintenance_endtime as endDay, u.display_name, c.id as appId, --m.id, b.user_id, nvl(yehz.dkye,0) as dkye from customer_application_info c inner join product_attribute p on c.product_id = p.id inner join basic_customer_information b on c.customer_id=b.id inner join sys_user u on b.user_id = u.id left join ty_customer_base base on b.TY_CUSTOMER_ID = base.ID left join ty_repay_tkmx tkmx on base.KHH = tkmx.KHH and p.product_type_code = tkmx.cpmc left join ty_repay_yehz yehz on yehz.JJH = tkmx.JJH --left join MAINTENANCE_PLAN m on m.app_id = c.id WHERE 1=1 and c.status in ('approved','end')
AND b.user_id in
#{item.userId}
AND b.CARD_ID=#{cardId}
AND b.CHINESE_NAME=#{customerName}
AND b.id=#{customerId}
AND c.product_id=#{productId}
AND b.user_id = #{customerManagerId}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

		  select  	   count(*)
				       from customer_application_info c
				       inner join product_attribute p on c.product_id = p.id
				       inner join basic_customer_information b  on c.customer_id=b.id
				       inner join sys_user u on b.user_id = u.id
				       --left join MAINTENANCE_PLAN m on m.app_id = c.id
				       left join ty_customer_base base on  b.TY_CUSTOMER_ID = base.ID
				       left join ty_repay_tkmx tkmx	   on base.KHH = tkmx.KHH  and p.product_type_code = tkmx.cpmc
				       left join ty_repay_yehz  yehz  on yehz.JJH = tkmx.JJH
					  WHERE 1=1 
					 	  and c.status in ('approved','end')
		  
 			AND b.user_id in  
	 		 
				#{item.userId} 
			
		
		
			AND b.CARD_ID=#{cardId}
		
					 
		
		    AND b.CHINESE_NAME=#{customerName}
		
					  
		
			AND b.user_id=#{customerManagerId}
		
		
			AND b.id=#{customerId}
		 
		 
			AND c.product_id=#{productId}

		
					SELECT * FROM
			(SELECT ROW_.*,ROWNUM ROWNUM_
				FROM (
					  select  b.chinese_name,
          					  b.card_id,
          					  p.product_name,
          					  m.maintenance_endtime-m.maintenance_day as startDay,
          					  m.maintenance_endtime as endDay,
				  			  u.display_name,
          					  c.id as appId,
          					  m.id,
          					  b.user_id,
          					  m.END_RESULT
				       from customer_application_info c
				       inner join product_attribute p on c.product_id = p.id
				       inner join basic_customer_information b  on c.customer_id=b.id
				       inner join sys_user u on b.user_id = u.id
				       left join MAINTENANCE_PLAN m on m.app_id = c.id
					  WHERE 1=1 
					  
 					  	AND b.user_id in  
	 					 
							#{item.userId} 
						
					
					
						AND b.CARD_ID=#{cardId}
					 
					 
					 
						AND b.CHINESE_NAME=#{customerName}
					 
					  
					  
						AND b.id=#{customerId}
					 
					 
						AND c.product_id=#{productId}
					 
					 
					 
						AND b.user_id = #{customerManagerId}
					 
					 
					 
						AND m.app_id = #{appId}
					 
					 
					 
		 		
							) ROW_
						WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
					WHERE ROWNUM_ > #{limit}*#{page}

		  select  	   count(*)
				       from customer_application_info c
				       inner join product_attribute p on c.product_id = p.id
				       inner join basic_customer_information b  on c.customer_id=b.id
				       inner join sys_user u on b.user_id = u.id
				       left join MAINTENANCE_PLAN m on m.app_id = c.id
					  WHERE 1=1 
		  
 			AND b.user_id in  
	 		 
				#{item.userId} 
			
		
		
			AND b.CARD_ID=#{cardId}
		
					 
		
		    AND b.CHINESE_NAME=#{customerName}
		
					  
		
			AND b.user_id=#{customerManagerId}
		
		
			AND b.id=#{customerId}
		 
		 
			AND c.product_id=#{productId}
		 
		 
		 
			AND m.app_id = #{appId}

select b.chinese_name, b.id as customer_id, b.card_id, p.product_name, c.APPLY_QUOTA, c.repay_way, c.repay_status, m.*, u.DISPLAY_NAME, b.user_id,c.product_id as productId from customer_application_info c inner join product_attribute p on c.product_id = p.id inner join basic_customer_information b on c.customer_id=b.id inner join sys_user u on b.user_id = u.id left join MAINTENANCE_PLAN m on m.customer_id = c.customer_id where 1=1
and m.id=#{id}
and c.id=#{appId}

SELECT * FROM
	(SELECT ROW_.*,ROWNUM ROWNUM_
		FROM (
			SELECT m.*,
				   b.chinese_name,
				   u.display_name,
				   (select count(*) from maintenance_plans_action a where a.maintenance_plan_id=m.id) as countAction
			  FROM MAINTENANCE_PLAN m
			  inner join
			  basic_customer_information b
			  on m.customer_id=b.id
			  inner join
			  sys_user u
			  on m.customer_manager_id = u.id
			  WHERE 1=1 AND m.customer_manager_id in 
			  
				#{item.userId}
			  
			 
				AND m.maintenance_goal=#{maintenanceGoal}
			 
			 
			 and m.maintenance_way=#{maintenanceWay}
			 
			 
				AND m.maintenance_dy=#{maintenanceDay}
			 
			 
				AND m.create_way=#{createWay}
			 
			 
				AND m.end_result=#{endResult}
			 
			 
				AND m.remarks_create_reason=#{remarksCreateReason}
			 
 		
					) ROW_
				WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
			WHERE ROWNUM_ > #{limit}*#{page}

select count(*)
from MAINTENANCE_PLAN m
where m.customer_manager_id=#{customerManagerId}
and m.end_result=#{result}

	
	and m.created_time<to_date(#{startTime},'yyyy-mm-dd hh24:mi:ss')
	


	
	and m.maintenance_endtime>to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss')

		
