mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select t.id, t.customer_id, b.ty_customer_id, b.chinese_name, t.product_id, p.product_name, b.card_id, t.apply_quota, t.status from customer_application_info t, basic_customer_information b, product_attribute p where t.customer_id = b.id and t.product_id = p.id
AND b.user_id in ${custManagerIds}
AND b.user_id = #{userId}
and p.product_name like '%'||#{productName}||'%'
and t.id like '%'||#{id}||'%'
and t.status= #{status}
and b.card_id like '%'||#{cardId}||'%'
and b.chinese_name like '%'||#{chineseName}||'%'
order by t.id asc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

		  select  count(*)
		          from customer_application_info  t,
		               basic_customer_information b,
		               product_attribute          p
		         where t.customer_id = b.id
		               and t.product_id = p.id
					   
 					  	AND b.user_id in ${custManagerIds}
					 
					 
					   
 					  	AND b.user_id = #{userId}
					 
					
					 
						and p.product_name like '%'||#{productName}||'%'
					 
					 
					 
						and t.id like '%'||#{id}||'%'
					 
					 
					 
						and t.status= #{status}
					 
					 
					 
						and b.card_id like '%'||#{cardId}||'%' 
					 
					  
					 
