mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT salary.*, usr.display_name as managerName FROM manager_salary salary INNER join sys_user usr ON usr.id = salary.customer_id WHERE 1=1
AND usr.display_name like '%${managerName}%'
AND salary.month=#{month}
AND salary.year=#{year}
order by salary.year desc,salary.month desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT count(*) FROM manager_salary salary INNER join sys_user usr ON usr.id = salary.customer_id WHERE 1=1
AND usr.display_name like '%${managerName}%'
AND salary.month=#{month}
AND salary.year=#{year}

mapper.insert:
insert into manager_salary (id, customer_id, reward_incentive_information, deduct_amount) 

	select #{item.id},#{item.customerId},#{item.rewardIncentiveInformation},#{item.deductAmount}
	from dual

mapper.select:
select return_prepare_amount from MANAGER_SALARY t
where t.year=#{year}
and t.month=#{month}
and t.customer_id=#{id}

select reward_incentive_information from MANAGER_SALARY t
where t.year=#{year}
and t.month=#{month}
and t.customer_id=#{id}
