SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT w.*, s.DISPLAY_NAME as displayName FROM
WEEKLY_ACCOUNT_MANAGER w, SYS_USER s WHERE 1 = 1
AND w.CUSTOMER_MANAGER_ID = s. ID
AND w.CUSTOMER_MANAGER_ID = #{loginId}
order by w.CREATED_TIME DESC
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT w.*, s.DISPLAY_NAME AS displayName FROM WEEKLY_ACCOUNT_MANAGER w, SYS_USER s WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
AND w.CUSTOMER_MANAGER_ID = s.ID
and w.CUSTOMER_MANAGER_ID in
#{item}
ORDER BY w.CREATED_TIME DESC
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT COUNT (*) FROM ( SELECT w.*, s.DISPLAY_NAME AS displayName FROM WEEKLY_ACCOUNT_MANAGER w, SYS_USER s WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
AND w.CUSTOMER_MANAGER_ID = s. ID AND ( w.CUSTOMER_MANAGER_ID IN ( SELECT T.CHILD_ID FROM manager_belong_map T LEFT JOIN account_manager_parameter amp ON amp. ID = T .child_id WHERE amp.user_id = #{loginId} ) ) ORDER BY w.CREATED_TIME DESC )

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT w.*, s.DISPLAY_NAME AS displayName FROM WEEKLY_ACCOUNT_MANAGER w, SYS_USER s WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
AND w.CUSTOMER_MANAGER_ID = s. ID AND ( w.CUSTOMER_MANAGER_ID IN ( select usr.id as id from (select * from sys_organization ) org inner join sys_department dep on org.id = dep.org_id inner join sys_dept_user dep_user on dep.id = dep_user.dept_id inner join sys_user usr on usr.id = dep_user.user_id and usr.user_type = 1 and usr.is_deleted = 0 INNER JOIN ( select sys_organization.id as orgId from sys_organization where id in (select t.org_id from sys_department t where t.id in(select tt.dept_id from sys_dept_user tt where tt.user_id =#{loginId})) ) aa ON org.id= aa.orgId ) ) ORDER BY w.CREATED_TIME DESC
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT COUNT (*) FROM ( SELECT w.*, s.DISPLAY_NAME AS displayName FROM WEEKLY_ACCOUNT_MANAGER w, SYS_USER s WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
AND w.CUSTOMER_MANAGER_ID = s. ID AND ( w.CUSTOMER_MANAGER_ID IN ( select usr.id as id from (select * from sys_organization ) org inner join sys_department dep on org.id = dep.org_id inner join sys_dept_user dep_user on dep.id = dep_user.dept_id inner join sys_user usr on usr.id = dep_user.user_id and usr.user_type = 1 and usr.is_deleted = 0 INNER JOIN ( select sys_organization.id as orgId from sys_organization where id in (select t.org_id from sys_department t where t.id in(select tt.dept_id from sys_dept_user tt where tt.user_id =#{loginId})) ) aa ON org.id= aa.orgId ) ) ORDER BY w.CREATED_TIME DESC )
